dataSet.query:
SELECT CASE &MeasurementsFrequency WHEN 1 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), HOUR) WHEN 2 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), DAY) WHEN 3 THEN CASE WHEN Measurements.MeasurementStartDate < &PeriodStartNumber THEN DATEADD(DATETIME(2015, 1, 1), SECOND, &PeriodStartNumber / 1000 - 63555667200 + &TimeZoneAdjustment) ELSE BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) END WHEN 4 THEN &PeriodNo END AS MeasurementPeriod, KeyOperations.Priority AS Priority, KeyOperations.KeyOperation AS KeyOperation, COUNT(*) AS MeasurementsCount, SUM(Measurements.RunTime) AS MeasurementsSum, 1 AS MeasurementID, (SUM(Measurements.RunTime * Measurements.RunTime) - 2 * AVG(Measurements.RunTime) * SUM(Measurements.RunTime) + COUNT(*) * (AVG(Measurements.RunTime) * AVG(Measurements.RunTime))) / COUNT(*) AS PopulationVariance, SUM(CASE WHEN Measurements.RunTime <= KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_T, SUM(CASE WHEN Measurements.RunTime > KeyOperations.ResponseTimeThreshold AND Measurements.RunTime <= 4 * KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_T_4T, SUM(CASE WHEN Measurements.RunTime > 4 * KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_4T, KeyOperations.ResponseTimeThreshold AS ResponseTimeThreshold, MIN(Measurements.RunTime) AS MinValue, MAX(Measurements.RunTime) AS MaxValue, Measurements.User, Measurements.CompletedWithError INTO MeasurementsCurrent FROM InformationRegister.TimeMeasurements AS Measurements INNER JOIN Catalog.KeyOperationProfiles.ProfileKeyOperations AS KeyOperations ON (KeyOperations.Ref = &Profile) AND Measurements.KeyOperation = KeyOperations.KeyOperation WHERE Measurements.MeasurementStartDate BETWEEN &PeriodStartNumber AND &PeriodEndNumber {WHERE (Measurements.RunTime >= &MeasurementLowerBoundaryCoefficient * KeyOperations.ResponseTimeThreshold), (Measurements.RunTime <= &MeasurementUpperBoundaryCoefficient * KeyOperations.ResponseTimeThreshold), Measurements.User, Measurements.CompletedWithError}
GROUP BY CASE &MeasurementsFrequency WHEN 1 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), HOUR) WHEN 2 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), DAY) WHEN 3 THEN CASE WHEN Measurements.MeasurementStartDate < &PeriodStartNumber THEN DATEADD(DATETIME(2015, 1, 1), SECOND, &PeriodStartNumber / 1000 - 63555667200 + &TimeZoneAdjustment) ELSE BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, Measurements.MeasurementStartDate / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) END WHEN 4 THEN &PeriodNo END, KeyOperations.KeyOperation, KeyOperations.Priority, KeyOperations.ResponseTimeThreshold, Measurements.User, Measurements.CompletedWithError
INDEX BY MeasurementPeriod, KeyOperation ;
//////////////////////////////////////////////////////////////////////////////// SELECT CASE &MeasurementsFrequency WHEN 1 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), HOUR) WHEN 2 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), DAY) WHEN 3 THEN CASE WHEN Measurements.MeasurementStartDate - (&PeriodStartNumber - &ComparisonPeriodStartNumber) < &PeriodStartNumber THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, &PeriodStartNumber / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) ELSE BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) END WHEN 4 THEN &PeriodNo END AS MeasurementPeriod, KeyOperations.Priority AS Priority, KeyOperations.KeyOperation AS KeyOperation, COUNT(*) AS MeasurementsCount, SUM(Measurements.RunTime) AS MeasurementsSum, 1 AS MeasurementID, (SUM(Measurements.RunTime * Measurements.RunTime) - 2 * AVG(Measurements.RunTime) * SUM(Measurements.RunTime) + COUNT(*) * (AVG(Measurements.RunTime) * AVG(Measurements.RunTime))) / COUNT(*) AS PopulationVariance, SUM(CASE WHEN Measurements.RunTime <= KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_T, SUM(CASE WHEN Measurements.RunTime > KeyOperations.ResponseTimeThreshold AND Measurements.RunTime <= 4 * KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_T_4T, SUM(CASE WHEN Measurements.RunTime > 4 * KeyOperations.ResponseTimeThreshold THEN 1 ELSE 0 END) AS N_4T, KeyOperations.ResponseTimeThreshold AS ResponseTimeThreshold, MIN(Measurements.RunTime) AS MinValue, MAX(Measurements.RunTime) AS MaxValue, Measurements.User, Measurements.CompletedWithError INTO MeasurementsPrevious FROM InformationRegister.TimeMeasurements AS Measurements INNER JOIN Catalog.KeyOperationProfiles.ProfileKeyOperations AS KeyOperations ON (KeyOperations.Ref = &Profile) AND Measurements.KeyOperation = KeyOperations.KeyOperation WHERE Measurements.MeasurementStartDate BETWEEN &ComparisonPeriodStartNumber AND &ComparisonPeriodEndNumber {WHERE Measurements.User, Measurements.CompletedWithError}
GROUP BY CASE &MeasurementsFrequency WHEN 1 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), HOUR) WHEN 2 THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), DAY) WHEN 3 THEN CASE WHEN Measurements.MeasurementStartDate - (&PeriodStartNumber - &ComparisonPeriodStartNumber) < &PeriodStartNumber THEN BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, &PeriodStartNumber / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) ELSE BEGINOFPERIOD(DATEADD(DATETIME(2015, 1, 1), SECOND, (Measurements.MeasurementStartDate + (&PeriodStartNumber - &ComparisonPeriodStartNumber)) / 1000 - 63555667200 + &TimeZoneAdjustment), WEEK) END WHEN 4 THEN &PeriodNo END, KeyOperations.KeyOperation, KeyOperations.Priority, KeyOperations.ResponseTimeThreshold, Measurements.User, Measurements.CompletedWithError
INDEX BY MeasurementPeriod, KeyOperation ;
//////////////////////////////////////////////////////////////////////////////// SELECT ISNULL(MeasurementsCurrent.Priority,MeasurementsPrevious.Priority) AS Priority, ISNULL(MeasurementsCurrent.MeasurementPeriod,MeasurementsPrevious.MeasurementPeriod) AS MeasurementPeriod, ISNULL(MeasurementsCurrent.KeyOperation,MeasurementsPrevious.KeyOperation) AS KeyOperation, MeasurementsCurrent.MeasurementsCount AS MeasurementsCount, MeasurementsPrevious.MeasurementsCount AS MeasurementsCountPrevious, MeasurementsCurrent.MeasurementsSum AS MeasurementsSum, MeasurementsPrevious.MeasurementsSum AS MeasurementsSumPrevious, MeasurementsCurrent.MeasurementID AS MeasurementID, MeasurementsPrevious.MeasurementID AS MeasurementIDPrevious, MeasurementsCurrent.PopulationVariance AS PopulationVariance, MeasurementsPrevious.PopulationVariance AS PopulationVariancePrevious, MeasurementsCurrent.N_T AS N_T, MeasurementsPrevious.N_T AS N_T_Previous, MeasurementsCurrent.N_T_4T AS N_T_4T, MeasurementsPrevious.N_T_4T AS N_T_4T_Next, MeasurementsCurrent.N_4T AS N_4T, MeasurementsPrevious.N_4T AS N_4T_Previous, ISNULL(MeasurementsCurrent.ResponseTimeThreshold, MeasurementsPrevious.ResponseTimeThreshold) AS ResponseTimeThreshold, MeasurementsCurrent.MinValue AS MinValue, MeasurementsPrevious.MinValue AS MinValuePrevious, MeasurementsCurrent.MaxValue AS MaxValue, MeasurementsPrevious.MaxValue AS MaxValuePrevious, ISNULL(MeasurementsCurrent.User, MeasurementsPrevious.User) AS User, ISNULL(MeasurementsCurrent.CompletedWithError, MeasurementsPrevious.CompletedWithError) AS CompletedWithError FROM MeasurementsCurrent AS MeasurementsCurrent {LEFT JOIN} MeasurementsPrevious AS MeasurementsPrevious ON (MeasurementsPrevious.MeasurementPeriod = MeasurementsCurrent.MeasurementPeriod) AND (MeasurementsPrevious.KeyOperation = MeasurementsCurrent.KeyOperation) AND (MeasurementsPrevious.User = MeasurementsCurrent.User) AND (MeasurementsPrevious.CompletedWithError = MeasurementsCurrent.CompletedWithError)
